/**
 * =============================
 *
 * Manages the GUI of the application.
 *
 * Create a webserver and a socket IO server.
 *
 * =============================
 *
 * Attributes : /
 *
 * Methods :
 * 		- start
 * 		- stop
 *
 * Events :
 * 		- connected
 * 		- disconnected
 * 		- stopped
 * 		- message(data)
 *
 * =============================
 */



/**
 * Load modules
 */

// Built-in
var fs = require('fs');
var http = require('http');
var io = require('socket.io');
// Global
var logger = require('logger');
var config = require('config');
var installerGenerator = require('installerGenerator');



/**
 * Variables
 */

// GUI host/port
var host = config.host;
var port = config.port;

// Pre-loaded frequently-user web-pages
var index = fs.readFileSync(__dirname + '/../frontend/index.html');



/**
 * Initialize the WebServer
 */

var server = http.createServer(function(req, res) {
	if (req.url == '/') {
		req.url = '/index.html';
	}
	fs.readFile(__dirname + '/../frontend' + req.url, function (err, data) {
		if (err) {
			res.writeHead(404);
			res.write(index);
		} else {
			res.writeHead(200);
			res.write(data);
		}
		res.end();
	});
});



/**
 * Set the WebServer handlers
 */

server.on('close', function () {
	logger.info('[GUI] GUI stopped');
});

server.on('listening', function () {
	logger.info('[GUI] GUI available at http://' + host + ':' + port + '/');
});



/**
 * Initialize Socket.io client socket
 */

var initClient = function (socket) {
	socket.on('generateInstaller', function (data) {
		installerGenerator.generateAgent(data.agentId, function (err) {
			if (err) logger.error('[GUI] Unable to generate installer for agent ' + data.agentId + ': ' + err.message);
		});
	});
};



/**
 * Public methods
 */

var start = function() {
	// Start webserver
	server.listen(port, host);

	// Socket IO
	io = io.listen(server, { log: false });
	io.set('log level', 1);

	// Socket IO handlers
	io.sockets.on('connection', function (socket) {
		initClient(socket);
	});
};

var stop = function (callback) {
	server.close(callback);
};



/**
 * Exports
 */

// methods
exports.start = start;
exports.stop = stop;